import { signOutFailure, SIGN_UP_START, signUpFailure, signUpSuccess, SIGN_UP_SUCCESS } from './userActions';
import { takeLatest, put, all, call } from 'redux-saga/effects';
import {
  signInFailure,
  signInSuccess,
  signOutSuccess,
  GOOGLE_SIGN_IN_START,
  EMAIL_SIGN_IN_START,
  CHECK_USER_SESSION,
  SIGN_OUT_START
} from './userActions';
import {
  auth,
  googleProvider,
  createUserProfileDocument,
  getCurrentUser
} from '../../firebase/firebase.utils';

export function* signOut(){
  try{
    yield auth.signOut();
    yield (put(signOutSuccess()))
  } catch(error){
    yield put(signOutFailure(error));
  }
}

export function* signUp(email: string, password: string, displayName: string){
  try{
    const { user } = yield auth.createUserWithEmailAndPassword(
      email,
      password
    );
    yield put(signUpSuccess(user,  { displayName }));
  } catch(error){
    yield put(signUpFailure(error));
  }
}

export function* signInAfterSignUp(user: any, additionalData: any){
  yield getSnapshotFromUserAuth(user, additionalData);
}

export function* getSnapshotFromUserAuth(userAuth: any, additionalData: any) {
  try {
    const userRef = yield call(createUserProfileDocument, userAuth, additionalData);
    const userSnapshot = yield userRef.get();
    yield put(
      signInSuccess({
        id: userSnapshot.id,
        ...userSnapshot.data()
      })
    );
  } catch (error) {
    yield put(signInFailure(error));
  }
}

export function* signInWithGoogle() {
  try {
    const { user } = yield auth.signInWithPopup(googleProvider);
    yield getSnapshotFromUserAuth(user);
  } catch (error) {
    yield put(signInFailure(error));
  }
}

export function* signInWithEmail(payload: any) {
  try {
    const { email, password } = payload;
    const { user } = yield auth.signInWithEmailAndPassword(email, password);
    yield getSnapshotFromUserAuth(user);
  } catch (error) {
    put(signInFailure(error));
  }
}

export function* isUserAuthenticated() {
  try {
    const userAuth = yield getCurrentUser();
    if (userAuth) {
      yield getSnapshotFromUserAuth(userAuth);
    }
  } catch (error) {
    yield put(signInFailure(error));
  }
}

export function* onGoogleSignInStart() {
  yield takeLatest(GOOGLE_SIGN_IN_START, signInWithGoogle);
}

export function* onEmailSignInStart() {
  yield takeLatest(EMAIL_SIGN_IN_START, signInWithEmail);
}

export function* checkUserSession() {
  yield takeLatest(CHECK_USER_SESSION, isUserAuthenticated);
}

export function* onSignOutStart(){
  yield takeLatest(SIGN_OUT_START, signOut);
}

export function* onSignUpStart(){
  yield takeLatest(SIGN_UP_START, signUp);
}

export function* onSignUpSuccess(){
  yield takeLatest(SIGN_UP_SUCCESS, signInAfterSignUp);
}

export function* userSagas() {
  yield all([
    call(onGoogleSignInStart),
    call(onEmailSignInStart),
    call(isUserAuthenticated),
    call(onSignOutStart),
    call(onSignUpStart)
  ]);
}
